export default function BookDetailPage() {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchBook = async () => {
      try {
        setLoading(true);
        const data = await bookService.getBookById(id);
        const book = data || {};
        setBook(book);
        setError('');
      } catch (err) {
        setError('도서를 불러올 수 없습니다.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    if (id) {
      fetchBook();
    }
  }, [id]);

  if (loading) {
    return (
      <Page>
        <Container>
          <LoadingSpinner>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="40"
              height="40"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
            >
              <circle cx="12" cy="12" r="10" />
            </svg>
          </LoadingSpinner>
        </Container>
      </Page>
    );
  }

  if (error || !book) {
    return (
      <Page>
        <Container>
          <BackLink to="/search">← 검색으로 돌아가기</BackLink>
          <ErrorState>
            <h2>오류 발생</h2>
            <p>{error || '도서를 찾을 수 없습니다.'}</p>
            <Link to="/search">검색 페이지로 이동</Link>
          </ErrorState>
        </Container>
      </Page>
    );
  }

  return (
    <Page>
      <Container>
        <BackLink to="/search">← 검색으로 돌아가기</BackLink>

        <Content>
          <CoverSection>
            <Cover></Cover>
            <ActionButtons>
              <button
                className="primary"
                onClick={() => alert('읽기 기능은 추후 업데이트됩니다.')}
              >
                온라인 읽기
              </button>
              <button className="secondary">다운로드 (PDF)</button>
              <button className="secondary">북마크</button>
            </ActionButtons>
          </CoverSection>

          <DetailSection>
            <Title>{book.title}</Title>

            <Meta>
              {book.author && (
                <div className="meta-item">
                  <strong>저자:</strong> {book.author}
                </div>
              )}
              {book.year && (
                <div className="meta-item">
                  <strong>출판연도:</strong> {book.year}
                </div>
              )}
              {book.language && (
                <div className="meta-item">
                  <strong>언어:</strong> {book.language}
                </div>
              )}
              {book.publisher && (
                <div className="meta-item">
                  <strong>출판사:</strong> {book.publisher}
                </div>
              )}
              {book.isbn && (
                <div className="meta-item">
                  <strong>ISBN:</strong> {book.isbn}
                </div>
              )}
            </Meta>

            {book.description && (
              <Description>
                <h3>설명</h3>
                <p>{book.description}</p>
              </Description>
            )}

            {book.categories && book.categories.length > 0 && (
              <div>
                <h3 style={{ marginBottom: '12px' }}>카테고리</h3>
                <Tags>
                  {book.categories.map((cat, i) => (
                    <div key={i} className="tag">
                      {cat}
                    </div>
                  ))}
                </Tags>
              </div>
            )}
          </DetailSection>
        </Content>
      </Container>
    </Page>
  );
}

