const generateSlideText = () => {
  const texts = [
    '세계의 고전 문학을 발견하세요',
    '저작권 만료된 도서들의 무한한 세계',
    '제목, 저자, 키워드로 검색하세요',
    '공개 도메인 도서의 보물창고',
    '고전의 가치를 현대에 담다',
    '무료로 읽는 세계 문학',
    '도서 탐색을 시작하세요',
    '문학의 거인들과 만나다',
    '시간을 초월한 이야기들',
    '지혜의 흔적을 따라가다',
  ];

  return texts.map((text, i) => `${text}\n`).join('');
};

export default function HomePage() {
  const [query, setQuery] = useState('');
  const navigate = useNavigate();

  const handleSearch = (e) => {
    e.preventDefault();
    if (query.trim()) {
      navigate(`/search?q=${encodeURIComponent(query)}`);
    }
  };

  const slideText = generateSlideText();

  return (
    <Page>
      <BackgroundSlide>
        <SlideContent>{slideText}</SlideContent>
        <SlideContent>{slideText}</SlideContent>
      </BackgroundSlide>

      <HeroSection>
        <Content>
          <Title>PublicDomain</Title>
          <Subtitle>
            세계의 공개 도메인 도서를 검색하고 무료로 읽어보세요.
          </Subtitle>

          <SearchContainer>
            <SearchForm onSubmit={handleSearch}>
              <SearchInput
                type="text"
                placeholder="제목, 저자, 키워드로 검색..."
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                autoFocus
              />
              <SearchButton type="submit">검색</SearchButton>
            </SearchForm>
          </SearchContainer>
        </Content>

        <Footer>
          수천 권의 도서를 무료로 탐색하세요
        </Footer>
      </HeroSection>
    </Page>
  );
}

