export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLocalLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      await authService.loginLocal(email, password);
      navigate('/');
    } catch (err) {
      const errorMsg = err?.message || err?.error || '로그인에 실패했습니다. 이메일과 비밀번호를 확인해주세요.';
      setError(errorMsg);
      console.error('Login error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleOAuthLogin = (provider) => {
    // 백엔드의 OAuth2 초기화 엔드포인트로 리다이렉트
    // 백엔드는 OAuth 제공자의 인증 페이지로 리다이렉트
    const apiBase = import.meta.env.VITE_API_BASE_URL || 'http://localhost:8080';
    window.location.href = `${apiBase}/oauth2/authorization/${provider}`;
  };

  return (
    <Page>
      <Container>
        <Card>
          <Title>로그인</Title>
          <Subtitle>PublicDomain 계정으로 로그인하세요</Subtitle>

          {error && <ErrorMessage>{error}</ErrorMessage>}

          <Form onSubmit={handleLocalLogin}>
            <FormGroup>
              <Label htmlFor="email">이메일</Label>
              <Input
                id="email"
                type="email"
                placeholder="your@email.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                disabled={loading}
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="password">비밀번호</Label>
              <Input
                id="password"
                type="password"
                placeholder="••••••••"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                disabled={loading}
              />
            </FormGroup>

            <SubmitButton type="submit" disabled={loading}>
              {loading ? '로그인 중...' : '로그인'}
            </SubmitButton>
          </Form>

          <Divider>또는</Divider>

          <div>
            <OAuthButton
              type="button"
              onClick={() => handleOAuthLogin('google')}
              disabled={loading}
            >
              Google로 로그인
            </OAuthButton>
            <OAuthButton
              type="button"
              onClick={() => handleOAuthLogin('naver')}
              disabled={loading}
            >
              Naver로 로그인
            </OAuthButton>
          </div>

          <LinkSection>
            <Link to="/register">회원가입</Link>
            <a href="#forgot">비밀번호 찾기</a>
          </LinkSection>
        </Card>
      </Container>
    </Page>
  );
}

