const ErrorMessage = styled.div`
  padding: ${({ theme }) => theme.spacing.sm} ${({ theme }) => theme.spacing.md};
  background: rgba(196, 30, 58, 0.1);
  border: 1px solid ${({ theme }) => theme.colors.error};
