export default function MyPage() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const data = await authService.fetchMe();
        setUser(data || {});
      } catch (error) {
        console.error('Failed to fetch user:', error);
        setUser(null);
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, []);

  const handleLogout = async () => {
    try {
      await authService.logout();
      navigate('/');
      window.location.reload();
    } catch (error) {
      console.error('로그아웃 실패:', error);
    }
  };

  if (loading) {
    return (
      <Page>
        <Container>
          <LoadingSpinner>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="40"
              height="40"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
            >
              <circle cx="12" cy="12" r="10" />
            </svg>
          </LoadingSpinner>
        </Container>
      </Page>
    );
  }

  if (!user) {
    return (
      <Page>
        <Container>
          <NotAuthenticatedMessage>
            <h2>로그인이 필요합니다</h2>
            <p>마이페이지에 접근하려면 먼저 로그인해주세요.</p>
            <Link to="/login">로그인하기</Link>
          </NotAuthenticatedMessage>
        </Container>
      </Page>
    );
  }

  const userInitial = user.username?.charAt(0).toUpperCase() || 'U';

  return (
    <Page>
      <Container>
        <Header>
          <h1>마이페이지</h1>
          <LogoutButton onClick={handleLogout}>로그아웃</LogoutButton>
        </Header>

        <Content>
          <Sidebar>
            <ProfileCard>
              <div className="avatar">{userInitial}</div>
              <h2>{user.username}</h2>
              <p>{user.email}</p>
            </ProfileCard>

            <NavCard>
              <Link to="/search"> 도서 검색</Link>
              <Link to="#bookmarks"> 북마크</Link>
              <Link to="#settings">⚙️ 설정</Link>
            </NavCard>
          </Sidebar>

          <MainSection>
            <Section>
              <h2>계정 정보</h2>
              <InfoGrid>
                <InfoItem>
                  <label>이름</label>
                  <p>{user.username}</p>
                </InfoItem>
                <InfoItem>
                  <label>이메일</label>
                  <p>{user.email}</p>
                </InfoItem>
                {user.authProvider && (
                  <InfoItem>
                    <label>가입 방식</label>
                    <p>
                      {user.authProvider === 'LOCAL'
                        ? '이메일 가입'
                        : user.authProvider}
                    </p>
                  </InfoItem>
                )}
                {user.createdAt && (
                  <InfoItem>
                    <label>가입 날짜</label>
                    <p>
                      {new Date(user.createdAt).toLocaleDateString('ko-KR')}
                    </p>
                  </InfoItem>
                )}
              </InfoGrid>
            </Section>

            <Section id="bookmarks">
              <h2>북마크한 도서</h2>
              <BookmarksList>
                {user.bookmarks && user.bookmarks.length > 0 ? (
                  user.bookmarks.map((book) => (
                    <BookmarkItem key={book.id} to={`/books/${book.id}`}>
                      <div className="title">{book.title}</div>
                      {book.author && (
                        <div className="author">저자: {book.author}</div>
                      )}
                    </BookmarkItem>
                  ))
                ) : (
                  <div className="empty">
                    <p>북마크한 도서가 없습니다.</p>
                    <Link to="/search">도서 검색하기</Link>
                  </div>
                )}
              </BookmarksList>
            </Section>

            <Section id="settings">
              <h2>계정 설정</h2>
              <InfoGrid>
                <button
                  style={{
                    padding: '12px 18px',
                    background: '#f5f5f5',
                    border: '1px solid #e3e3e3',
                    cursor: 'pointer',
                  }}
                >
                  비밀번호 변경
                </button>
                <button
                  style={{
                    padding: '12px 18px',
                    background: '#f5f5f5',
                    border: '1px solid #e3e3e3',
                    cursor: 'pointer',
                  }}
                >
                  개인정보 수정
                </button>
              </InfoGrid>
            </Section>
          </MainSection>
        </Content>
      </Container>
    </Page>
  );
}

