`;

export default function RegisterPage() {
  const [form, setForm] = useState({
    username: '',
    email: '',
    password: '',
    passwordConfirm: '',
    language: 'ko',
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (form.password !== form.passwordConfirm) {
      setError('비밀번호가 일치하지 않습니다.');
      return;
    }

    if (form.password.length < 6) {
      setError('비밀번호는 최소 6자 이상이어야 합니다.');
      return;
    }

    setLoading(true);

    try {
      await authService.registerLocal(
        form.email,
        form.username,
        form.password,
        form.language
      );
      alert('회원가입이 완료되었습니다. 로그인해주세요.');
      navigate('/login');
    } catch (err) {
      const errorMsg = err?.message || err?.error || '회원가입에 실패했습니다. 다시 시도해주세요.';
      setError(errorMsg);
      console.error('Register error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Page>
      <Container>
        <Card>
          <Title>회원가입</Title>
          <Subtitle>PublicDomain 계정을 만들어보세요</Subtitle>

          {error && <ErrorMessage>{error}</ErrorMessage>}

          <Form onSubmit={handleSubmit}>
            <FormGroup>
              <Label htmlFor="username">이름</Label>
              <Input
                id="username"
                type="text"
                placeholder="홍길동"
                name="username"
                value={form.username}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="email">이메일</Label>
              <Input
                id="email"
                type="email"
                placeholder="your@email.com"
                name="email"
                value={form.email}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="password">비밀번호</Label>
              <Input
                id="password"
                type="password"
                placeholder="••••••••"
                name="password"
                value={form.password}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="passwordConfirm">비밀번호 확인</Label>
              <Input
                id="passwordConfirm"
                type="password"
                placeholder="••••••••"
                name="passwordConfirm"
                value={form.passwordConfirm}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="language">언어</Label>
              <Select
                id="language"
                name="language"
                value={form.language}
                onChange={handleChange}
                disabled={loading}
              >
                <option value="ko">한국어</option>
                <option value="en">English</option>
                <option value="ja">日本語</option>
                <option value="zh">中文</option>
              </Select>
            </FormGroup>

            <SubmitButton type="submit" disabled={loading}>
              {loading ? '가입 중...' : '가입하기'}
            </SubmitButton>
          </Form>

          <LoginLink>
            이미 계정이 있으신가요?
            <Link to="/login">로그인</Link>
          </LoginLink>
        </Card>
      </Container>
    </Page>
  );
}

import React, { useState, useEffect } from 'react';
import { useSearchParams, Link } from 'react-router-dom';
import styled from 'styled-components';
import { bookService } from '../services';

const Page = styled.div`
  width: 100%;
  min-height: calc(100vh - 64px);
  background: ${({ theme }) => theme.colors.background};
  padding: ${({ theme }) => theme.spacing.xl} ${({ theme }) => theme.spacing.lg};
`;

const Container = styled.div`
  max-width: ${({ theme }) => theme.layout.maxWidth};
  margin: 0 auto;
`;

const Header = styled.div`
  margin-bottom: ${({ theme }) => theme.spacing.xxl};
`;

const Title = styled.h1`
  margin-bottom: ${({ theme }) => theme.spacing.md};
`;

const SearchBox = styled.form`
  display: flex;
  gap: ${({ theme }) => theme.spacing.md};
  margin-bottom: ${({ theme }) => theme.spacing.xl};

  @media (max-width: ${({ theme }) => theme.breakpoints.sm}) {
    flex-direction: column;
  }
`;

const SearchInput = styled.input`
  flex: 1;
  padding: ${({ theme }) => theme.spacing.sm} ${({ theme }) => theme.spacing.md};
  border: 2px solid ${({ theme }) => theme.colors.border};
  font-size: 1rem;
  font-family: inherit;
  transition: all ${({ theme }) => theme.transitions.fast};

  &:focus {
    outline: none;
    border-color: ${({ theme }) => theme.colors.primary};
    box-shadow: 0 0 0 3px rgba(26, 26, 26, 0.05);
  }

  &:hover {
    border-color: ${({ theme }) => theme.colors.primary};
  }
`;

const SearchButton = styled.button`
  padding: ${({ theme }) => theme.spacing.sm} ${({ theme }) => theme.spacing.lg};
  background: ${({ theme }) => theme.colors.primary};
  color: ${({ theme }) => theme.colors.white};
  border: none;
  font-weight: 600;
  cursor: pointer;
  transition: all ${({ theme }) => theme.transitions.fast};
  white-space: nowrap;

  &:hover:not(:disabled) {
    background: ${({ theme }) => theme.colors.accentAlt};
    transform: translateY(-2px);
    box-shadow: 0 8px 20px rgba(26, 26, 26, 0.2);
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
  }
`;

const ResultsSection = styled.div`
  margin-top: ${({ theme }) => theme.spacing.xl};
`;

const ResultCount = styled.p`
  color: ${({ theme }) => theme.colors.muted};
  margin-bottom: ${({ theme }) => theme.spacing.lg};
  font-size: 0.95rem;
`;

const ResultsList = styled.div`
  display: grid;
  gap: ${({ theme }) => theme.spacing.lg};
`;

const ResultCard = styled(Link)`
  display: block;
  padding: ${({ theme }) => theme.spacing.lg};
  background: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.border};
  transition: all ${({ theme }) => theme.transitions.normal};
  text-decoration: none;

  &:hover {
    box-shadow: ${({ theme }) => theme.shadows.medium};
    transform: translateY(-2px);
  }

  .title {
    font-size: 1.1rem;
    font-weight: 700;
    color: ${({ theme }) => theme.colors.primary};
    margin-bottom: ${({ theme }) => theme.spacing.sm};
  }

  .author {
    color: ${({ theme }) => theme.colors.muted};
    margin-bottom: ${({ theme }) => theme.spacing.sm};
    font-size: 0.9rem;
  }

  .description {
    color: ${({ theme }) => theme.colors.textSecondary};
    line-height: 1.6;
    margin-bottom: ${({ theme }) => theme.spacing.sm};
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
    font-size: 0.95rem;
  }

  .meta {
    display: flex;
    gap: ${({ theme }) => theme.spacing.md};
    flex-wrap: wrap;
    font-size: 0.85rem;
    color: ${({ theme }) => theme.colors.muted};

    span {
      display: flex;
      align-items: center;
      gap: 4px;
    }
  }
`;

const EmptyState = styled.div`
  text-align: center;
  padding: ${({ theme }) => theme.spacing.xxl};
  color: ${({ theme }) => theme.colors.textSecondary};

  h3 {
    margin-bottom: ${({ theme }) => theme.spacing.md};
  }

  p {
    margin-bottom: ${({ theme }) => theme.spacing.xl};
  }
`;

const Pagination = styled.div`
  display: flex;
  gap: ${({ theme }) => theme.spacing.sm};
  justify-content: center;
  margin-top: ${({ theme }) => theme.spacing.xl};
  flex-wrap: wrap;

  button,
  a {
    padding: ${({ theme }) => theme.spacing.sm} ${({ theme }) => theme.spacing.md};
    border: 1px solid ${({ theme }) => theme.colors.border};
    background: ${({ theme }) => theme.colors.surface};
    cursor: pointer;
    transition: all ${({ theme }) => theme.transitions.fast};

    &:hover:not(:disabled) {
      border-color: ${({ theme }) => theme.colors.primary};
      background: ${({ theme }) => theme.colors.highlight};
    }

    &.active {
      background: ${({ theme }) => theme.colors.primary};
      color: ${({ theme }) => theme.colors.white};
      border-color: ${({ theme }) => theme.colors.primary};
    }

    &:disabled {
      opacity: 0.5;
      cursor: not-allowed;
    }
  }
`;

const LoadingSpinner = styled.div`
  text-align: center;
  padding: ${({ theme }) => theme.spacing.xl};
  color: ${({ theme }) => theme.colors.muted};

  @keyframes spin {
    to {
      transform: rotate(360deg);
    }
  }

  svg {
    animation: spin 1s linear infinite;
    display: inline-block;
  }
`;

export default function SearchPage() {
  const [searchParams, setSearchParams] = useSearchParams();
  const [query, setQuery] = useState(searchParams.get('q') || '');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [page, setPage] = useState(0);
  const [totalResults, setTotalResults] = useState(0);
  const pageSize = 20;

  const handleSearch = async (e) => {
    e.preventDefault();
    setPage(0);
    await performSearch(query, 0);
  };

  const performSearch = async (searchQuery, pageNum) => {
    if (!searchQuery.trim()) {
      setResults([]);
      setError('검색어를 입력해주세요.');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const data = await bookService.searchBooks(
        searchQuery,
        pageNum,
        pageSize
      );

      const results = Array.isArray(data) ? data : (data.books || data.results || []);
      setResults(results);
      setTotalResults(results.length || 0);
      setSearchParams({ q: searchQuery, page: pageNum });
    } catch (err) {
      setError('검색 중 오류가 발생했습니다. 다시 시도해주세요.');
      console.error('Search error:', err);
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const q = searchParams.get('q');
    const p = parseInt(searchParams.get('page') || '0');
    if (q) {
      setQuery(q);
      setPage(p);
      performSearch(q, p);
    }
  }, []);

  const totalPages = Math.ceil(totalResults / pageSize);
  const hasNextPage = page < totalPages - 1;
  const hasPrevPage = page > 0;

  return (
    <Page>
      <Container>
        <Header>
          <Title>도서 검색</Title>
          <SearchBox onSubmit={handleSearch}>
            <SearchInput
              type="text"
              placeholder="제목, 저자, 키워드로 검색..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              disabled={loading}
            />
            <SearchButton type="submit" disabled={loading}>
              {loading ? '검색 중...' : '검색'}
            </SearchButton>
          </SearchBox>
        </Header>

        {error && (
          <EmptyState>
            <h3>검색 오류</h3>
            <p>{error}</p>
          </EmptyState>
        )}

        {loading && !results.length && (
          <LoadingSpinner>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="32"
              height="32"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
            >
              <circle cx="12" cy="12" r="10" />
            </svg>
          </LoadingSpinner>
        )}

        {results.length > 0 && (
          <ResultsSection>
            <ResultCount>
              총 {totalResults.toLocaleString()}개의 결과
            </ResultCount>

            <ResultsList>
              {results.map((book) => (
                <ResultCard key={book.id} to={`/books/${book.id}`}>
                  <div className="title">{book.title}</div>
                  {book.author && <div className="author">저자: {book.author}</div>}
                  {book.description && (
                    <div className="description">{book.description}</div>
                  )}
                  <div className="meta">
                    {book.year && <span> {book.year}</span>}
                    {book.language && <span> {book.language}</span>}
                  </div>
                </ResultCard>
              ))}
            </ResultsList>

            {totalPages > 1 && (
              <Pagination>
                <button
                  onClick={() => performSearch(query, page - 1)}
                  disabled={!hasPrevPage}
                >
                  ← 이전
                </button>

                {Array.from({ length: Math.min(totalPages, 5) }, (_, i) => (
                  <button
                    key={i}
                    onClick={() => performSearch(query, i)}
                    className={page === i ? 'active' : ''}
                  >
                    {i + 1}
                  </button>
                ))}

                <button
                  onClick={() => performSearch(query, page + 1)}
                  disabled={!hasNextPage}
                >
                  다음 →
                </button>
              </Pagination>
            )}
          </ResultsSection>
        )}

        {!loading && results.length === 0 && !error && query && (
          <EmptyState>
            <h3>검색 결과가 없습니다</h3>
            <p>"{query}"에 해당하는 도서를 찾을 수 없습니다.</p>
            <p>다른 검색어로 시도해보세요.</p>
          </EmptyState>
        )}

        {!loading && results.length === 0 && !error && !query && (
          <EmptyState>
            <h3>도서를 검색해보세요</h3>
            <p>제목, 저자, 키워드로 퍼블릭 도메인 도서를 찾아보세요.</p>
          </EmptyState>
        )}
      </Container>
    </Page>
  );
}

